import { useState, useEffect, useRef } from "react";

// ─── Glitch Text ──────────────────────────────────────────────────────────────
export const GlitchText = ({ text, className = "" }) => {
  const [glitching, setGlitching] = useState(false);
  useEffect(() => {
    const interval = setInterval(() => {
      setGlitching(true);
      setTimeout(() => setGlitching(false), 200);
    }, 3500);
    return () => clearInterval(interval);
  }, []);
  return (
    <span className={className} style={{ position: "relative", display: "inline-block" }}>
      <span style={{ position: "relative", zIndex: 1 }}>{text}</span>
      {glitching && (
        <>
          <span style={{
            position: "absolute", top: 0, left: 0, color: "#ff2d78",
            clipPath: "polygon(0 20%, 100% 20%, 100% 40%, 0 40%)",
            transform: "translate(-2px, 0)", opacity: 0.8, zIndex: 2,
          }}>{text}</span>
          <span style={{
            position: "absolute", top: 0, left: 0, color: "#00f5ff",
            clipPath: "polygon(0 60%, 100% 60%, 100% 80%, 0 80%)",
            transform: "translate(2px, 0)", opacity: 0.8, zIndex: 2,
          }}>{text}</span>
        </>
      )}
    </span>
  );
};

// ─── Cyber Button ─────────────────────────────────────────────────────────────
export const CyberButton = ({ children, onClick, primary = false, style = {} }) => (
  <button
    onClick={onClick}
    style={{
      position: "relative",
      padding: "12px 32px",
      background: primary ? "linear-gradient(135deg, #00f5ff22, #7b2fff22)" : "transparent",
      border: `1px solid ${primary ? "#00f5ff" : "#ffffff33"}`,
      color: primary ? "#00f5ff" : "#fff",
      fontFamily: "'Courier New', monospace",
      fontSize: "12px",
      letterSpacing: "3px",
      textTransform: "uppercase",
      cursor: "pointer",
      transition: "all 0.3s ease",
      clipPath: "polygon(8px 0%, 100% 0%, calc(100% - 8px) 100%, 0% 100%)",
      boxShadow: primary ? "0 0 20px #00f5ff33, inset 0 0 20px #00f5ff11" : "none",
      ...style,
    }}
    onMouseEnter={e => {
      e.currentTarget.style.boxShadow = `0 0 30px ${primary ? "#00f5ff66" : "#ffffff22"}, inset 0 0 20px ${primary ? "#00f5ff22" : "#ffffff11"}`;
      e.currentTarget.style.transform = "translateY(-2px)";
    }}
    onMouseLeave={e => {
      e.currentTarget.style.boxShadow = primary ? "0 0 20px #00f5ff33, inset 0 0 20px #00f5ff11" : "none";
      e.currentTarget.style.transform = "translateY(0)";
    }}
  >
    {children}
  </button>
);

// ─── Section Title ────────────────────────────────────────────────────────────
export const SectionTitle = ({ label, title }) => (
  <div style={{ marginBottom: "60px" }}>
    <div style={{
      fontFamily: "'Courier New', monospace",
      fontSize: "12px",
      letterSpacing: "4px",
      color: "#00f5ff",
      marginBottom: "12px",
      textTransform: "uppercase",
    }}>
      {label}
    </div>
    <h2 style={{
      fontSize: "clamp(32px, 5vw, 52px)",
      fontFamily: "'Courier New', monospace",
      color: "#fff",
      letterSpacing: "2px",
      lineHeight: 1.1,
      position: "relative",
      display: "inline-block",
    }}>
      {title}
      <span style={{
        position: "absolute", bottom: "-8px", left: 0,
        width: "40%", height: "2px",
        background: "linear-gradient(90deg, #00f5ff, transparent)",
        boxShadow: "0 0 10px #00f5ff",
      }} />
    </h2>
  </div>
);

// ─── Skill Bar ────────────────────────────────────────────────────────────────
export const SkillBar = ({ skill, delay }) => {
  const [width, setWidth] = useState(0);
  const ref = useRef();
  useEffect(() => {
    const observer = new IntersectionObserver(([entry]) => {
      if (entry.isIntersecting) setTimeout(() => setWidth(skill.level), delay);
    }, { threshold: 0.5 });
    if (ref.current) observer.observe(ref.current);
    return () => observer.disconnect();
  }, [skill.level, delay]);
  return (
    <div ref={ref} style={{ marginBottom: "20px" }}>
      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "8px" }}>
        <span style={{ fontFamily: "'Courier New', monospace", fontSize: "13px", letterSpacing: "2px", color: "#aaa" }}>
          {skill.name}
        </span>
        <span style={{ fontFamily: "'Courier New', monospace", fontSize: "12px", color: skill.color }}>
          {skill.level}%
        </span>
      </div>
      <div style={{ height: "4px", background: "#ffffff11", position: "relative", overflow: "hidden" }}>
        <div style={{
          height: "100%",
          width: `${width}%`,
          background: `linear-gradient(90deg, ${skill.color}88, ${skill.color})`,
          boxShadow: `0 0 10px ${skill.color}`,
          transition: "width 1.2s cubic-bezier(0.16, 1, 0.3, 1)",
        }} />
      </div>
    </div>
  );
};

// ─── Particle Field ───────────────────────────────────────────────────────────
export const ParticleField = () => {
  const canvasRef = useRef();
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext("2d");
    const resize = () => {
      canvas.width = canvas.offsetWidth;
      canvas.height = canvas.offsetHeight;
    };
    resize();
    window.addEventListener("resize", resize);
    const particles = Array.from({ length: 60 }, () => ({
      x: Math.random() * canvas.width,
      y: Math.random() * canvas.height,
      vx: (Math.random() - 0.5) * 0.4,
      vy: (Math.random() - 0.5) * 0.4,
      size: Math.random() * 2 + 0.5,
      opacity: Math.random() * 0.5 + 0.1,
      color: ["#00f5ff", "#7b2fff", "#00ff9f"][Math.floor(Math.random() * 3)],
    }));
    let animId;
    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      particles.forEach(p => {
        p.x += p.vx; p.y += p.vy;
        if (p.x < 0 || p.x > canvas.width) p.vx *= -1;
        if (p.y < 0 || p.y > canvas.height) p.vy *= -1;
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.size, 0, Math.PI * 2);
        ctx.fillStyle = p.color + Math.floor(p.opacity * 255).toString(16).padStart(2, "0");
        ctx.fill();
      });
      particles.forEach((p, i) => {
        particles.slice(i + 1).forEach(q => {
          const dist = Math.hypot(p.x - q.x, p.y - q.y);
          if (dist < 120) {
            ctx.beginPath();
            ctx.moveTo(p.x, p.y);
            ctx.lineTo(q.x, q.y);
            ctx.strokeStyle = `#00f5ff${Math.floor((1 - dist / 120) * 40).toString(16).padStart(2, "0")}`;
            ctx.lineWidth = 0.5;
            ctx.stroke();
          }
        });
      });
      animId = requestAnimationFrame(draw);
    };
    draw();
    return () => {
      cancelAnimationFrame(animId);
      window.removeEventListener("resize", resize);
    };
  }, []);
  return (
    <canvas ref={canvasRef} style={{
      position: "absolute", inset: 0, width: "100%", height: "100%", pointerEvents: "none",
    }} />
  );
};
