export class World {
  constructor(width, height) {
    this.width = width;
    this.height = height;
    this.zones = [
      { x: 100, y: 100, width: 150, height: 150, color: "rgba(255, 0, 0, 0.2)", label: "Projects", id: "projects" },
      { x: 400, y: 100, width: 150, height: 150, color: "rgba(0, 255, 0, 0.2)", label: "Skills", id: "skills" },
      { x: 100, y: 400, width: 150, height: 150, color: "rgba(0, 0, 255, 0.2)", label: "Experience", id: "experience" },
      { x: 400, y: 400, width: 150, height: 150, color: "rgba(255, 255, 0, 0.2)", label: "About", id: "about" },
    ];
  }

  checkCollision(player) {
    for (let zone of this.zones) {
      if (
        player.x < zone.x + zone.width &&
        player.x + player.size > zone.x &&
        player.y < zone.y + zone.height &&
        player.y + player.size > zone.y
      ) {
        return zone;
      }
    }
    return null;
  }

  draw(ctx) {
    // Draw floor
    ctx.fillStyle = "#1a1a1a";
    ctx.fillRect(0, 0, this.width, this.height);

    // Draw Grid
    ctx.strokeStyle = "#333";
    ctx.lineWidth = 1;
    const gridSize = 50;

    for (let x = 0; x <= this.width; x += gridSize) {
      ctx.beginPath();
      ctx.moveTo(x, 0);
      ctx.lineTo(x, this.height);
      ctx.stroke();
    }

    for (let y = 0; y <= this.height; y += gridSize) {
      ctx.beginPath();
      ctx.moveTo(0, y);
      ctx.lineTo(this.width, y);
      ctx.stroke();
    }

    // Draw Zones
    this.zones.forEach(zone => {
      ctx.fillStyle = zone.color;
      ctx.fillRect(zone.x, zone.y, zone.width, zone.height);

      ctx.fillStyle = "#fff";
      ctx.font = "20px Arial";
      ctx.textAlign = "center";
      ctx.fillText(zone.label, zone.x + zone.width / 2, zone.y + zone.height / 2);
    });
  }
}
