import { useState, useEffect } from "react";
import { CyberButton, ParticleField } from "./UI";

const ROLES = [
  "Full-Stack Developer",
  "React.js Specialist",
  "Django API Engineer",
  "Solution Analyst @ Argusoft",
];

const BADGES = [
  { label: "React.js",   x: "8%",  y: "12%", color: "#00f5ff" },
  { label: "Django",     x: "68%", y: "8%",  color: "#7b2fff" },
  { label: "Node.js",    x: "78%", y: "72%", color: "#00ff9f" },
  { label: "PostgreSQL", x: "0%",  y: "76%", color: "#ff2d78" },
];

export default function Hero({ onNav }) {
  const [typedText, setTypedText] = useState("");
  const [roleIndex, setRoleIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);

  useEffect(() => {
    const current = ROLES[roleIndex];
    const speed = isDeleting ? 40 : 80;
    const timeout = setTimeout(() => {
      if (!isDeleting && charIndex < current.length) {
        setTypedText(current.slice(0, charIndex + 1));
        setCharIndex(c => c + 1);
      } else if (!isDeleting && charIndex === current.length) {
        setTimeout(() => setIsDeleting(true), 1500);
      } else if (isDeleting && charIndex > 0) {
        setTypedText(current.slice(0, charIndex - 1));
        setCharIndex(c => c - 1);
      } else {
        setIsDeleting(false);
        setRoleIndex(r => (r + 1) % ROLES.length);
      }
    }, speed);
    return () => clearTimeout(timeout);
  }, [charIndex, isDeleting, roleIndex]);

  return (
    <section id="home" style={{
      minHeight: "100vh",
      display: "flex",
      alignItems: "center",
      position: "relative",
      overflow: "hidden",
      padding: "0 5%",
    }}>
      <ParticleField />

      {/* Scan line */}
      <div style={{
        position: "absolute", left: 0, right: 0,
        height: "2px",
        background: "linear-gradient(90deg, transparent, #00f5ff22, transparent)",
        animation: "scanline 6s linear infinite",
        pointerEvents: "none", zIndex: 1,
      }} />

      {/* Grid overlay */}
      <div style={{
        position: "absolute", inset: 0,
        backgroundImage: `
          linear-gradient(#00f5ff06 1px, transparent 1px),
          linear-gradient(90deg, #00f5ff06 1px, transparent 1px)
        `,
        backgroundSize: "60px 60px",
        pointerEvents: "none",
      }} />

      <div style={{ maxWidth: "1200px", margin: "0 auto", width: "100%", position: "relative", zIndex: 2 }}>
        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "80px", alignItems: "center" }}>

          {/* Left — Text */}
          <div style={{ animation: "fadeUp 0.8s ease forwards" }}>
            <div style={{
              fontFamily: "'Courier New', monospace",
              fontSize: "12px", letterSpacing: "6px",
              color: "#00f5ff", marginBottom: "20px", opacity: 0.8,
            }}>
              &gt;_ INITIALIZING PORTFOLIO...
            </div>

            <h1 style={{
              fontSize: "clamp(44px, 6vw, 80px)",
              fontFamily: "'Courier New', monospace",
              lineHeight: 1, letterSpacing: "-1px", marginBottom: "16px",
            }}>
              <span style={{ color: "#fff" }}>ANSHIK</span>
              <br />
              <span style={{
                color: "transparent",
                WebkitTextStroke: "1px #00f5ff",
                textShadow: "0 0 40px #00f5ff44",
              }}>THAKUR</span>
            </h1>

            <div style={{
              height: "32px", marginBottom: "32px",
              fontSize: "15px", color: "#7b2fff",
              fontFamily: "'Courier New', monospace", letterSpacing: "2px",
              display: "flex", alignItems: "center", gap: "8px",
            }}>
              <span style={{ color: "#00f5ff88" }}>//</span>
              <span>{typedText}</span>
              <span style={{ animation: "blink 1s infinite", color: "#00f5ff" }}>|</span>
            </div>

            <p style={{
              color: "#666", fontSize: "15px", lineHeight: 1.8,
              marginBottom: "44px", maxWidth: "480px", fontFamily: "Georgia, serif",
            }}>
              Full Stack Developer specializing in React.js and Django. I build scalable
              applications, architect custom UI systems, and write clean code that lasts.
            </p>

            <div style={{ display: "flex", gap: "16px", flexWrap: "wrap" }}>
              <CyberButton primary onClick={() => onNav("Projects")}>View Projects</CyberButton>
              <CyberButton onClick={() => onNav("Contact")}>Get In Touch</CyberButton>
            </div>

            {/* Stats */}
            <div style={{ marginTop: "60px", display: "flex", gap: "40px" }}>
              {[["2+", "Years Exp."], ["2", "Key Projects"], ["30%", "API Speedup"]].map(([num, label]) => (
                <div key={label}>
                  <div style={{
                    fontSize: "28px", fontFamily: "'Courier New', monospace",
                    color: "#00f5ff", textShadow: "0 0 20px #00f5ff88",
                  }}>{num}</div>
                  <div style={{ fontSize: "11px", color: "#555", letterSpacing: "2px" }}>{label}</div>
                </div>
              ))}
            </div>
          </div>

          {/* Right — Orb */}
          <div style={{
            display: "flex", justifyContent: "center", alignItems: "center",
            position: "relative", height: "500px",
          }}>
            <div className="hero-orb" style={{
              width: "260px", height: "260px", borderRadius: "50%",
              background: "linear-gradient(135deg, #00f5ff11, #7b2fff22)",
              border: "1px solid #00f5ff33",
              display: "flex", alignItems: "center", justifyContent: "center",
              position: "relative",
            }}>
              {[320, 380, 440].map((size, i) => (
                <div key={size} style={{
                  position: "absolute", width: size, height: size, borderRadius: "50%",
                  border: `1px solid ${["#00f5ff", "#7b2fff", "#00ff9f"][i]}${["22", "18", "12"][i]}`,
                  animation: `spin ${[8, 12, 16][i]}s linear infinite${i % 2 ? " reverse" : ""}`,
                }} />
              ))}
              <div style={{ fontSize: "72px", textShadow: "0 0 40px #00f5ff", zIndex: 1 }}>⟨/⟩</div>
            </div>

            {BADGES.map((badge, i) => (
              <div key={badge.label} style={{
                position: "absolute", left: badge.x, top: badge.y,
                padding: "6px 14px",
                border: `1px solid ${badge.color}44`,
                color: badge.color, fontSize: "11px", letterSpacing: "2px",
                background: `${badge.color}11`,
                clipPath: "polygon(6px 0%, 100% 0%, calc(100% - 6px) 100%, 0% 100%)",
                animation: `float ${3 + i * 0.8}s ease-in-out infinite`,
                animationDelay: `${i * 0.5}s`,
              }}>
                {badge.label}
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}
