import { useState } from "react";
import { SectionTitle } from "./UI";
import { PROJECTS } from "../data";

function ProjectCard({ project }) {
  const [hovered, setHovered] = useState(false);

  return (
    <div
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      style={{
        position: "relative",
        padding: "36px",
        background: hovered
          ? `linear-gradient(135deg, ${project.color}11, #0a0a0f)`
          : "linear-gradient(135deg, #0d0d1a, #0a0a0f)",
        border: `1px solid ${hovered ? project.color : "#ffffff11"}`,
        transition: "all 0.4s ease",
        boxShadow: hovered ? `0 0 40px ${project.color}22, inset 0 0 40px ${project.color}08` : "none",
        cursor: "default",
      }}
    >
      {/* Corner accent */}
      <div style={{
        position: "absolute", top: 0, right: 0,
        width: "60px", height: "60px",
        background: `linear-gradient(135deg, transparent 50%, ${project.color}22 50%)`,
        opacity: hovered ? 1 : 0.4,
        transition: "opacity 0.3s",
      }} />

      {/* Subtitle badge */}
      <div style={{
        display: "inline-block",
        padding: "4px 12px",
        border: `1px solid ${project.color}33`,
        color: project.color,
        fontFamily: "'Courier New', monospace",
        fontSize: "10px", letterSpacing: "2px",
        background: `${project.color}0d`,
        marginBottom: "20px",
      }}>
        {project.subtitle}
      </div>

      <div style={{
        fontSize: "30px", marginBottom: "14px", color: project.color,
        textShadow: hovered ? `0 0 20px ${project.color}` : "none",
        transition: "text-shadow 0.3s",
      }}>
        {project.icon}
      </div>

      <h3 style={{
        fontFamily: "'Courier New', monospace",
        fontSize: "20px", color: "#fff",
        marginBottom: "12px", letterSpacing: "2px",
      }}>
        {project.title}
      </h3>

      <p style={{
        fontSize: "14px", color: "#777", lineHeight: 1.8,
        marginBottom: "24px", fontFamily: "Georgia, serif",
      }}>
        {project.description}
      </p>

      {/* Highlights */}
      <ul style={{ listStyle: "none", padding: 0, marginBottom: "24px" }}>
        {project.highlights.map((h, i) => (
          <li key={i} style={{
            color: "#555", fontSize: "13px", marginBottom: "6px",
            paddingLeft: "16px", position: "relative", fontFamily: "Georgia, serif",
          }}>
            <span style={{ position: "absolute", left: 0, color: project.color }}>▸</span>
            {h}
          </li>
        ))}
      </ul>

      {/* Tags */}
      <div style={{ display: "flex", gap: "8px", flexWrap: "wrap" }}>
        {project.tags.map(tag => (
          <span key={tag} style={{
            padding: "4px 12px",
            border: `1px solid ${project.color}44`,
            color: project.color,
            fontFamily: "'Courier New', monospace",
            fontSize: "11px", letterSpacing: "1px",
            background: `${project.color}11`,
          }}>
            {tag}
          </span>
        ))}
      </div>
    </div>
  );
}

export default function Projects() {
  return (
    <section id="projects" style={{
      padding: "100px 5%",
      maxWidth: "1200px",
      margin: "0 auto",
    }}>
      <SectionTitle label="// 003 — WORK" title="PROJECTS" />
      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "28px" }}>
        {PROJECTS.map(project => (
          <ProjectCard key={project.title} project={project} />
        ))}
      </div>
    </section>
  );
}
