import { SectionTitle, SkillBar, ScrollReveal } from "./UI";
import { SKILLS, SECONDARY_SKILLS } from "../data";

export default function Skills() {
  return (
    <section id="skills" style={{
      padding: "100px 5%",
      background: "linear-gradient(180deg, transparent, #0a0a1a, transparent)",
    }}>
      <div style={{ maxWidth: "1200px", margin: "0 auto" }}>
        <SectionTitle label="// 002 â€” SKILLS" title="TECH STACK" />

        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "16px 60px", marginBottom: "48px" }}>
          {SKILLS.map((skill, i) => (
            <ScrollReveal key={skill.name} delay={i * 100} direction={i % 2 === 0 ? "left" : "right"}>
              <SkillBar skill={skill} delay={i * 150} />
            </ScrollReveal>
          ))}
        </div>

        <div style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(110px, 1fr))",
          gap: "12px",
        }}>
          {SECONDARY_SKILLS.map((tech, i) => (
            <ScrollReveal key={tech} delay={i * 50} direction="up">
              <div style={{
                padding: "12px",
                border: "1px solid #ffffff08",
                textAlign: "center",
                fontFamily: "'Courier New', monospace",
                fontSize: "11px",
                color: "#555",
                letterSpacing: "1px",
                transition: "all 0.3s",
                cursor: "default",
                position: "relative",
                overflow: "hidden",
              }}
                onMouseEnter={e => {
                  e.currentTarget.style.color = "#00f5ff";
                  e.currentTarget.style.borderColor = "#00f5ff33";
                  e.currentTarget.style.background = "#00f5ff0a";
                  e.currentTarget.style.transform = "translateY(-3px)";
                  e.currentTarget.style.boxShadow = "0 4px 15px #00f5ff15";
                }}
                onMouseLeave={e => {
                  e.currentTarget.style.color = "#555";
                  e.currentTarget.style.borderColor = "#ffffff08";
                  e.currentTarget.style.background = "transparent";
                  e.currentTarget.style.transform = "translateY(0)";
                  e.currentTarget.style.boxShadow = "none";
                }}
              >
                {tech}
              </div>
            </ScrollReveal>
          ))}
        </div>
      </div>
    </section>
  );
}
