import { SectionTitle, ScrollReveal } from "./UI";
import { EXPERIENCE } from "../data";
import { useState } from "react";

export default function About() {
  return (
    <section id="about" style={{
      padding: "120px 5% 80px",
      maxWidth: "1200px",
      margin: "0 auto",
    }}>
      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "80px", alignItems: "flex-start" }}>

        {/* Left ‚Äî Bio */}
        <ScrollReveal direction="left">
          <div>
            <SectionTitle label="// 001 ‚Äî ABOUT" title="WHO AM I" />

            <p style={{ color: "#777", fontSize: "15px", lineHeight: 1.9, marginBottom: "24px", fontFamily: "Georgia, serif" }}>
              I'm a Full Stack Developer at Argusoft, working at the intersection of complex UI engineering and robust backend design. My day-to-day involves leading React.js features, mentoring teammates, and pushing frontend architecture further.
            </p>
            <p style={{ color: "#666", fontSize: "15px", lineHeight: 1.9, marginBottom: "32px", fontFamily: "Georgia, serif" }}>
              I love hard problems ‚Äî I built an entire drag-and-drop engine from scratch with zero external libraries. Clean, maintainable code and real-world scalability are what I'm all about.
            </p>

            <div style={{
              padding: "24px",
              border: "1px solid #00f5ff22",
              background: "linear-gradient(135deg, #00f5ff08, transparent)",
              clipPath: "polygon(12px 0%, 100% 0%, calc(100% - 12px) 100%, 0% 100%)",
              transition: "all 0.3s",
              cursor: "default",
            }}
            onMouseEnter={e => {
              e.currentTarget.style.borderColor = "#00f5ff55";
              e.currentTarget.style.boxShadow = "0 0 30px #00f5ff22";
            }}
            onMouseLeave={e => {
              e.currentTarget.style.borderColor = "#00f5ff22";
              e.currentTarget.style.boxShadow = "none";
            }}
            >
              <div style={{
                fontFamily: "'Courier New', monospace",
                fontSize: "12px", color: "#00f5ff", letterSpacing: "3px", marginBottom: "10px",
              }}>
                STATUS: OPEN TO OPPORTUNITIES
              </div>
              <div style={{ color: "#555", fontSize: "13px", marginBottom: "6px" }}>
                üìç India ¬∑ Remote-friendly
              </div>
              <div style={{ color: "#555", fontSize: "13px" }}>
                üéì B.Tech CSE ¬∑ GNDEC Ludhiana ¬∑ CGPA 8.3
              </div>
            </div>
          </div>
        </ScrollReveal>

        {/* Right ‚Äî Experience Timeline */}
        <ScrollReveal direction="right">
          <div>
            <div style={{
              fontFamily: "'Courier New', monospace",
              fontSize: "12px", letterSpacing: "4px",
              color: "#00f5ff", marginBottom: "32px", textTransform: "uppercase",
            }}>
              // Experience
            </div>

            <div style={{ position: "relative" }}>
              {/* Vertical line */}
              <div style={{
                position: "absolute", left: "12px", top: 0, bottom: 0,
                width: "1px",
                background: "linear-gradient(180deg, #00f5ff44, #7b2fff44, transparent)",
              }} />

              {EXPERIENCE.map((exp, i) => (
                <TimelineItem key={exp.role} exp={exp} index={i} />
              ))}
            </div>
          </div>
        </ScrollReveal>
      </div>
    </section>
  );
}

function TimelineItem({ exp, index }) {
  const [hovered, setHovered] = useState(false);

  return (
    <ScrollReveal delay={index * 300} direction="right">
      <div
        onMouseEnter={() => setHovered(true)}
        onMouseLeave={() => setHovered(false)}
        style={{
          paddingLeft: "44px",
          paddingBottom: "40px",
          position: "relative",
          cursor: "default",
        }}
      >
        {/* Dot */}
        <div style={{
          position: "absolute", left: "6px", top: "6px",
          width: hovered ? "18px" : "14px",
          height: hovered ? "18px" : "14px",
          marginLeft: hovered ? "-2px" : "0",
          marginTop: hovered ? "-2px" : "0",
          borderRadius: "50%",
          background: exp.color,
          boxShadow: hovered
            ? `0 0 20px ${exp.color}, 0 0 40px ${exp.color}66`
            : `0 0 12px ${exp.color}`,
          transition: "all 0.3s",
        }} />

        <div style={{
          fontFamily: "'Courier New', monospace",
          fontSize: "11px", letterSpacing: "2px",
          color: exp.color, marginBottom: "6px",
        }}>
          {exp.period}
        </div>
        <div style={{
          fontFamily: "'Courier New', monospace",
          fontSize: "16px", color: hovered ? "#fff" : "#ccc",
          marginBottom: "2px", letterSpacing: "1px",
          textShadow: hovered ? `0 0 10px ${exp.color}44` : "none",
          transition: "all 0.3s",
        }}>
          {exp.role}
        </div>
        <div style={{
          fontFamily: "'Courier New', monospace",
          fontSize: "12px", color: "#555",
          marginBottom: "16px", letterSpacing: "1px",
        }}>
          {exp.company}
        </div>

        <ul style={{ listStyle: "none", padding: 0 }}>
          {exp.points.map((pt, j) => (
            <li key={j} style={{
              color: "#666", fontSize: "13px", lineHeight: 1.7,
              marginBottom: "8px", fontFamily: "Georgia, serif",
              paddingLeft: "16px", position: "relative",
              transform: hovered ? `translateX(4px)` : "translateX(0)",
              transition: `transform 0.3s ease ${j * 0.05}s`,
            }}>
              <span style={{
                position: "absolute", left: 0, color: exp.color, fontSize: "10px",
              }}>‚ñ∏</span>
              {pt}
            </li>
          ))}
        </ul>
      </div>
    </ScrollReveal>
  );
}
