export class Player {
  constructor(x, y, size, speed) {
    this.x = x;
    this.y = y;
    this.size = size;
    this.speed = speed;
    this.vx = 0;
    this.vy = 0;
  }

  update(input, world) {
    this.vx = 0;
    this.vy = 0;

    if (input.keys.ArrowUp || input.keys.w) this.vy = -this.speed;
    if (input.keys.ArrowDown || input.keys.s) this.vy = this.speed;
    if (input.keys.ArrowLeft || input.keys.a) this.vx = -this.speed;
    if (input.keys.ArrowRight || input.keys.d) this.vx = this.speed;

    // Predicted next position
    let nextX = this.x + this.vx;
    let nextY = this.y + this.vy;

    // Boundary checks
    if (nextX < 0) nextX = 0;
    if (nextX + this.size > world.width) nextX = world.width - this.size;
    if (nextY < 0) nextY = 0;
    if (nextY + this.size > world.height) nextY = world.height - this.size;

    this.x = nextX;
    this.y = nextY;
  }

  draw(ctx) {
    ctx.fillStyle = "#00f5ff";
    ctx.fillRect(this.x, this.y, this.size, this.size);

    // Add a simple glow effect
    ctx.shadowBlur = 10;
    ctx.shadowColor = "#00f5ff";
  }
}
